const isAdmin=document.querySelector("#wpadminbar");const singleProductAbout=document.querySelector(".single-product-about");const singleProductPage=document.querySelector(".rh-single-product");const homePage=document.querySelector(".home-page");const archivePage=document.querySelector(".category");const singleProduct=document.querySelector(".single-product");const faqPage=document.querySelector(".faq");const privacyPage=document.querySelector(".privacy");const pricingPage=document.querySelector(".pricing");const affiliatePage=document.querySelector(".affiliate-page");const aboutPage=document.querySelector(".about-us-page");const body=document.body;const breadcrumbs=document.querySelector(".breadcrumb-container");const skillset=document.querySelector(".product-skillset");const sidebarContent=document.querySelector(".content-with-sidebar");const projectAccordion=document.querySelector(".project-accordion");onload=()=>{if(homePage){componentCards();heroScroller();clientsScroller();testimonialsScroller();canvasBackground()}if(singleProductPage){const loader=document.querySelector(".loader");loader.style.display="none";singleProductPage.classList.remove("preload-fader");coverIframe();startSlick();ratingHover();bookMeeting()}if(archivePage){componentSlider()}if(faqPage){console.log("faq page");faqAccordion()}if(privacyPage){console.log("privacy page");privacyTabs()}if(pricingPage){console.log("pricing page");pricingSwitch()}if(affiliatePage){console.log("affiliate page");affiliateSlider()}if(aboutPage){console.log("about us page");aboutSlider()}if(breadcrumbs){const arrowLeft=breadcrumbs.querySelector(".breadcrumb-arrow-left");const arrowRight=breadcrumbs.querySelector(".breadcrumb-arrow-right");const breadcrumbWindow=breadcrumbs.querySelector(".breadcrumb-window");const breadcrumbText=breadcrumbs.querySelector(".woocommerce-breadcrumb");arrowLeft.addEventListener("click",breadcrumbArrowHandler.bind(null,"left"),false);arrowRight.addEventListener("click",breadcrumbArrowHandler.bind(null,"right"),false);function breadcrumbArrowHandler(param){const coords=breadcrumbText.offsetWidth-breadcrumbWindow.offsetWidth;if(param==="left"){breadcrumbText.style.transform="translateX(0)"}else if(param==="right"){breadcrumbText.style.transform="translateX(-"+coords+"px)"}}}if(skillset){const title=skillset.querySelector(".accordion-title");const skills=skillset.querySelector(".employee-skills");skills.style.maxHeight=skills.scrollHeight+"px";title.addEventListener("click",handleCollapse,false);document.addEventListener("scroll",handleScroll,false);function handleCollapse(){if(this.classList.contains("collapsed")){skills.style.maxHeight=skills.scrollHeight+"px";skills.style.marginBlock="1rem 2rem";skills.style.opacity=1}else{skills.style.opacity=0;skills.style.maxHeight=0;skills.style.marginBlock="1rem 0"}this.classList.toggle("collapsed")}function handleScroll(){if(!title.classList.contains("collapsed")){title.classList.add("collapsed");skills.style.maxHeight=0;skills.style.marginBlock="1rem 0"}}}if(sidebarContent){const btn=sidebarContent.querySelector(".hide-btn");const sidebar=sidebarContent.querySelector(".sidebar");const cards=sidebarContent.querySelector(".products");btn.addEventListener("click",hideSidebar,false);function hideSidebar(){gsap.to(cards,{duration:.2,opacity:0,onComplete:gsapComplete});function gsapComplete(){btn.classList.toggle("hidden");sidebarContent.classList.toggle("fullSize");sidebar.classList.toggle("closed");setTimeout(()=>{gsap.to(cards,{duration:.2,opacity:1})},200)}}}if(projectAccordion){const titles=projectAccordion.querySelectorAll(".project-accordion-title");const contents=projectAccordion.querySelectorAll(".project-accordion-content");titles.forEach(title=>{title.addEventListener("click",handleProjectAccordion,false)});function handleProjectAccordion(){const content=this.nextElementSibling;const verticalPadding=8;if(this.classList.contains("open")){content.style.maxHeight=0;content.style.paddingBlock=0;content.style.opacity=0}else{content.style.maxHeight=content.scrollHeight+verticalPadding*2+"px";content.style.paddingBlock="1rem";content.style.opacity=1}this.classList.toggle("open")}}if(singleProductAbout){const aboutText=singleProductAbout.querySelector(".about-text");const showMoreBtn=singleProductAbout.querySelector(".show-more");const maxHeight=aboutText.scrollHeight;const initialText=aboutText.innerText;const textArr=initialText.split(".");const transitionTime=.5;if(textArr.length>5){showMoreBtn.style.display="block";const introText=textArr.splice(1,5).join(".")+"...";const theRestText=textArr.splice(5).join(".");aboutText.innerText=introText;const lesserHeight=aboutText.scrollHeight;aboutText.style.maxHeight=lesserHeight+"px";showMoreBtn.addEventListener("click",handleShowMoreBtn,false);function handleShowMoreBtn(){if(this.classList.contains("open")){aboutText.style.maxHeight=lesserHeight+"px";setTimeout(()=>{aboutText.innerText=introText},transitionTime*1e3);showMoreBtn.innerText="Show more"}else{aboutText.innerText=introText.slice(0,-2)+theRestText;aboutText.style.maxHeight=maxHeight+"px";showMoreBtn.innerText="Show less"}this.classList.toggle("open")}}}};onresize=()=>{if(affiliatePage){affiliateSlider()}};function noScroll(){body.classList.add("stop-scroll")}function addScroll(){body.classList.remove("stop-scroll")}function canvasBackground(){const canvas=document.querySelector("canvas");const ctx=canvas.getContext("2d");const options=acf.get("circleQuantity");let w=canvas.width=640;let h=canvas.height=480;const circles=[];const colors=options.circle_colors.split(",");const circleNum=options.circle_number;const minSpeed=options.min_speed;const maxSpeed=options.max_speed;const minRadius=options.min_radius;const maxRadius=options.max_radius;function calculateSize(){w=canvas.width=parseInt(getComputedStyle(canvas).width);h=canvas.height=parseInt(getComputedStyle(canvas).height)}onresize=calculateSize;class Circle{constructor(x,y,vx,vy,diameter,color){this.x=x;this.y=y;this.vx=vx;this.vy=vy;this.diameter=diameter;this.color=color}drawSelf(){ctx.beginPath();ctx.fillStyle=this.color;ctx.strokeStyle=this.color;ctx.arc(this.x,this.y,this.diameter,0,2*Math.PI);ctx.stroke();ctx.fill()}moveSelf(){if(this.x>w+this.diameter||this.x<this.diameter*-1){this.vx*=-1}if(this.y>h+this.diameter||this.y<this.diameter*-1){this.vy*=-1}this.x+=this.vx;this.y+=this.vy}}function createCircles(quantity){let counter=quantity;while(counter>0){const randomX=Math.floor(Math.random()*w);const randomY=Math.floor(Math.random()*h);const randomVX=Math.random()*maxSpeed-minSpeed;const randomVY=Math.random()*maxSpeed-minSpeed;const randomRadius=Math.random()*maxRadius+minRadius;const randomColor=Math.floor(Math.random()*colors.length);circles.push(new Circle(randomX,randomY,randomVX,randomVY,randomRadius,colors[randomColor]));counter--}}calculateSize();createCircles(circleNum);circles.forEach(c=>{c.drawSelf()});setInterval(()=>{ctx.clearRect(0,0,w,h);circles.forEach(c=>{c.moveSelf();c.drawSelf()})},50);console.log(`circle number: ${options.circle_number}`);console.log(`circle colors: ${options.circle_colors}`);console.log(`circle speed: ${options.min_speed} - ${options.max_speed}`);console.log(`circle radius: ${options.min_radius} - ${options.max_radius}`)}function bookMeeting(){const interviewButton=document.querySelector(".setup-interview");const whiteBackdrop=document.querySelector(".white-backdrop");const close=document.querySelector(".white-backdrop > i");interviewButton.addEventListener("click",e=>{e.preventDefault();body.style.position="relative";if(isAdmin){whiteBackdrop.style.top=window.scrollY-32+"px"}else{whiteBackdrop.style.top=window.scrollY+"px"}noScroll();whiteBackdrop.style.display="grid"});close.addEventListener("click",()=>{addScroll();whiteBackdrop.style.display="none"})}function ratingHover(){const stars=document.querySelectorAll(".comment-form-rating i");const container=document.querySelector(".comment-form-rating");stars.forEach((star,index)=>{star.addEventListener("mouseover",function(){addStars(stars,index)});star.addEventListener("click",function(){freezeStars(container)});container.addEventListener("mouseout",removeAllStars,false)})}function addStars(stars,index){Array.from(stars).forEach((item,idx)=>{if(idx<=index){item.classList.add("yellow")}else{item.classList.remove("yellow")}})}function removeAllStars(){const stars=document.querySelectorAll(".comment-form-rating i");stars.forEach(star=>{star.classList.remove("yellow")})}function freezeStars(container){container.removeEventListener("mouseout",removeAllStars,false)}function heroScroller(){const scroller=document.querySelector(".scroller");const words=["Designers","Developers","Managers","Employees","Assistants"];let counter=1;setInterval(()=>{if(counter>words.length-1){counter=0}scroller.textContent=words[counter];counter++},3e3)}function clientsScroller(){jQuery(".clients-slider").slick({slidesToShow:5,slidesToScroll:1,arrows:false,dots:true,autoplay:true,autoplaySpeed:5e3,draggable:true,infinite:true,swipeToSlide:true,responsive:[{breakpoint:800,settings:{slidesToShow:3}},{breakpoint:450,settings:{slidesToShow:2}}]})}function testimonialsScroller(){jQuery(".testimonials-slider").slick({slidesToShow:1,slidesToScroll:1,arrows:false,dots:true,draggable:true,infinite:true,swipeToSlide:true,adaptiveHeight:true})}function coverIframe(){const cover=document.querySelector(".iframe-cover");const iframe=document.querySelector(".iframe-container > iframe");if(cover){cover.addEventListener("click",()=>{iframe.src+="?autoplay=1";setTimeout(()=>{cover.classList.add("hide")},1e3)})}}function startSlick(){jQuery(".single-product ul.products").slick({dots:true,arrows:false,infinite:true,speed:300,slidesToShow:4,slidesToScroll:1,autoplay:true,autoplaySpeed:3e3,responsive:[{breakpoint:1350,settings:{slidesToShow:3}},{breakpoint:1050,settings:{slidesToShow:2}},{breakpoint:768,settings:{centerPadding:"40px",slidesToShow:1}}]})}function faqAccordion(){const accordionHeaders=document.querySelectorAll(".accordion-header");const accordionBodies=document.querySelectorAll(".accordion-body");accordionHeaders.forEach(item=>{item.addEventListener("click",e=>{const accordionBody=e.target.nextElementSibling;const accordionOpen=getComputedStyle(accordionBody,null).getPropertyValue("display");if(accordionOpen==="none"){accordionHeaders.forEach(item=>{item.firstElementChild.classList.remove("rotate")});accordionBodies.forEach(item=>{jQuery(item).slideUp("fast")})}const body=e.target.nextElementSibling;const arrow=e.target.firstElementChild;arrow.classList.toggle("rotate");jQuery(body).slideToggle("fast")})})}function privacyTabs(){const tabs=document.querySelectorAll(".privacy-tab");const contents=document.querySelectorAll(".content");tabs.forEach(item=>{item.addEventListener("click",e=>{const content=Array.from(contents);tabs.forEach(item=>{item.classList.remove("active")});e.target.classList.add("active");content.forEach(item=>{item.classList.remove("shown")});const found=content.find(item=>{return item.dataset.content===e.target.dataset.tab});found.classList.add("shown")})})}function pricingSwitch(){const priceSwitch=document.querySelector(".switch");const switchDesc=document.querySelectorAll(".switch-desc");const spans=document.querySelectorAll(".pricing-switch>span");priceSwitch.addEventListener("click",()=>{switchDesc.forEach(item=>{item.classList.toggle("active")});spans.forEach(item=>{item.classList.toggle("active")})})}function affiliateSlider(){jQuery(".affiliate-cards").slick({arrows:true,slidesToShow:1,slidesToScroll:1,autoplay:false,mobileFirst:true,responsive:[{breakpoint:767,settings:"unslick"}]})}function aboutSlider(){jQuery(".gallery-viewport").slick({slidesToShow:1,slidesToScroll:1,arrows:false,fade:true,asNavFor:".about-gallery"});jQuery(".about-gallery").slick({slidesToShow:5,slidesToScroll:1,asNavFor:".gallery-viewport",dots:true,arrows:false,centerMode:true,focusOnSelect:true,variableWidth:true,autoplay:true,autoplaySpeed:2e3,responsive:[{breakpoint:1e3,settings:{slidesToShow:3}}]});jQuery(".gallery-gallery-mobile").slick({slidesToShow:1,slidesToScroll:1,dots:true,arrows:false,mobileFirst:true,adaptiveHeight:true})}function componentSlider(){jQuery(".component__slider").slick({slidesToShow:1,slidesToScroll:1,dots:true,arrows:false,fade:true,cssEase:"linear",autoplay:true,autoplaySpeed:2e3});jQuery(".rh-query-results").slick({responsive:[{breakpoint:5e3,settings:"unslick"},{breakpoint:768,settings:{slidesToShow:1,slidesToScroll:1,variableWidth:true,centerMode:true,arrows:false,autoplay:true,autoplaySpeed:2e3}}]})}function componentCards(){jQuery(".employees").slick({responsive:[{breakpoint:5e3,settings:"unslick"},{breakpoint:768,settings:{slidesToShow:1,slidesToScroll:1,variableWidth:true,centerMode:true,arrows:false,autoplay:true,autoplaySpeed:2e3}}]})}